import { apiResources } from "../api/apiResources";
import { endPoints } from "../api/endpoints";
import { request } from "../httpRequest";
import { IItems, IPostItem } from "../interfaces/items";
import   * as queryStringify from "querystringify";

export const getOrder =async(token:string,orderId:string)=>{
    const options={
        headers:{
            "Authorization":"Bearer " + token,
            "Content-Type":"application/json"
        }
    }
    const responseItems = await request(endPoints.api_root_url+apiResources.orders+"/"+orderId,options)
    return responseItems;
}