import { apiResources } from "../api/apiResources";
import { endPoints } from "../api/endpoints";
import { request } from "../httpRequest";
 import   * as queryStringify from "querystringify";
import { IQuery } from "../interfaces/query.interface";

export const getListingType=async (token:string, query:any, siteId:number) => {
    const queryString:string = queryStringify.stringify(query,true);
    const options={
        headers:{
            "Authorization":"Bearer "+token,
            "Content-Type":"application/json"
        }
    }
    
    const responseItems = await request(endPoints.api_root_url+apiResources.sites+"/"+siteId+"/listing_type"+queryString,options);   
    return responseItems;
}