import { apiResources } from "../api/apiResources";
import { endPoints } from "../api/endpoints";
import { request } from "../httpRequest";
import { IItems, IPostItem } from "../interfaces/items";
import   * as queryStringify from "querystringify";

export const getResource =async(token:string,resource:string,method:string = "GET")=>{
    const options={
        headers:{

            "Authorization":"Bearer " + token,
            "Content-Type":"application/json"

        },
        method:method
    }
    const responseItems = await request(endPoints.api_root_url+resource,options)
    return responseItems;
}